}
void main() { gl_Position = vert(); }

glsl3.frag:
precision highp float;
vec4 CCSampleWithAlphaSeparated(sampler2D tex, vec2 uv) {
#if CC_USE_EMBEDDED_ALPHA
  return vec4(texture(tex, uv).rgb, texture(tex, uv + vec2(0.0, 0.5)).r);
#else
  return texture(tex, uv);
#endif
}
#if USE_ALPHA_TEST
  layout(std140) uniform ALPHA_TEST_DATA {
    float alphaThreshold;
  };
#endif
layout(std140) uniform CCGlobal {
  highp   vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_nativeSize;
  mediump vec4 cc_probeInfo;
  mediump vec4 cc_debug_view_mode;
};
layout(std140) uniform CCCamera {
  highp   mat4 cc_matView;
  highp   mat4 cc_matViewInv;
  highp   mat4 cc_matProj;
  highp   mat4 cc_matProjInv;
  highp   mat4 cc_matViewProj;
  highp   mat4 cc_matViewProjInv;
  highp   vec4 cc_cameraPos;
  mediump vec4 cc_surfaceTransform;
  mediump vec4 cc_screenScale;
  mediump vec4 cc_exposure;
  mediump vec4 cc_mainLitDir;
  mediump vec4 cc_mainLitColor;
  mediump vec4 cc_ambientSky;
  mediump vec4 cc_ambientGround;
  mediump vec4 cc_fogColor;
  mediump vec4 cc_fogBase;
  mediump vec4 cc_fogAdd;
  mediump vec4 cc_nearFar;
  mediump vec4 cc_viewPort;
};
in vec4 color;
in vec2 uv0;
uniform sampler2D mainTexture;
layout(std140) uniform MXD {
  vec4 mainColor;
  vec4 iMouse;
  vec2 resolution;
};
#if USE_TEXTURE
    uniform sampler2D cc_spriteTexture;
#endif
#define iTime cc_time.x
#define iResolution resolution
#define USE_MAX_RADIUS 2
#define USE_DOUBLE_HASH 0
#define USE_HASHSCALE1 .1031
#define USE_HASHSCALE3 vec3(.1031, .1030, .0973)
float hash12(vec2 p)
{
  vec3 p3  = fract(vec3(p.xyx) * USE_HASHSCALE1);
    p3 += dot(p3, p3.yzx + 19.19);
    return fract((p3.x + p3.y) * p3.z);
}
vec2 hash22(vec2 p)
{
  vec3 p3 = fract(vec3(p.xyx) * USE_HASHSCALE3);
    p3 += dot(p3, p3.yzx+19.19);
    return fract((p3.xx+p3.yz)*p3.zy);
}
vec4 frag() {
  vec4 o = vec4(1, 1, 1, 1);
  #if USE_TEXTURE
    o *= CCSampleWithAlphaSeparated(cc_spriteTexture, uv0);
  #endif
  vec2 fragCoord = uv0 * resolution;
  float r = 10. * exp2(-3.*iMouse.x/iResolution.x);
  vec2 uv = fragCoord.xy / iResolution.y * r;
  vec2 p0 = floor(uv);
  vec2 circles = vec2(0.);
  for (int j = -USE_MAX_RADIUS; j <= USE_MAX_RADIUS; ++j)
  {
      for (int i = -USE_MAX_RADIUS; i <= USE_MAX_RADIUS; ++i)
      {
    vec2 pi = p0 + vec2(i, j);
          #if USE_DOUBLE_HASH
          vec2 hsh = hash22(pi);
          #else
          vec2 hsh = pi;
          #endif
          vec2 p = pi + hash22(hsh);
          float t = fract(0.3*iTime + hash12(hsh));
          vec2 v = p - uv;
          float d = length(v) - (float(USE_MAX_RADIUS) + 1.)*t;
          float h = 1e-3;
          float d1 = d - h;
          float d2 = d + h;
          float p1 = sin(31.*d1) * smoothstep(-0.6, -0.3, d1) * smoothstep(0., -0.3, d1);
          float p2 = sin(31.*d2) * smoothstep(-0.6, -0.3, d2) * smoothstep(0., -0.3, d2);
          circles += 0.5 * normalize(v) * ((p2 - p1) / (2. * h) * (1. - t) * (1. - t));
      }
  }
  circles /= float((USE_MAX_RADIUS*2+1)*(USE_MAX_RADIUS*2+1));
  float intensity = mix(0.01, 0.15, smoothstep(0.1, 0.6, abs(fract(0.05*iTime + 0.5)*2.-1.)));
  vec3 n = vec3(circles, sqrt(1. - dot(circles, circles)));
  vec3 color = texture(mainTexture, uv/r - intensity*n.xy).rgb + 5.*pow(clamp(dot(n, normalize(vec3(1., 0.7, 0.5))), 0., 1.), 6.);
  o = vec4(color, 1.0);
  return o;
}
layout(location = 0) out vec4 cc_FragColor;
void main() { cc_FragColor = frag(); }

